import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-past-events',
  templateUrl: './past-events.component.html',
  styleUrls: ['./past-events.component.scss']
})
export class pasteventsComponent implements OnInit {
  events: any[] = [];

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    this.fetchEvents();
  }

  fetchEvents(): void {
    this.http.get<any[]>('http://localhost:8000/eventgames').subscribe(
      (data) => {
        this.events = data;
      },
      (error) => {
        console.error('Error fetching events:', error);
      }
    );
  }
}
<h2>Eventos Pasados</h2>
<div *ngFor="let event of events">
  <div class="event-card">
    <h2>{{ event.title }}</h2>
    <p>{{ event.description }}</p>
    <p>Availability: {{ event.availability === 'no' ? 'Open' : 'Closed' }}</p>
    <p>Limit Participants: {{ event.limitparticipants }}</p>
    <p>Publication Date: {{ event.publicationevent }}</p>
  </div>
</div>
