import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { UserService } from '../../../services/user.service';

interface User {
  id: number;
  username: string;
  password: string;
}

@Component({
  selector: 'app-loginregister',
  templateUrl: './loginregister.component.html',
  styleUrls: ['./loginregister.component.scss']
})
export class LoginregisterComponent {
  username: string = '';
  password: string = '';
  errorMessage: string = '';

  constructor(private router: Router, private http: HttpClient, private userService: UserService) {}

  login() {
    this.http.get<User[]>('http://localhost:8000/users').subscribe((users: User[]) => {
      const foundUser = users.find(u => u.username === this.username && u.password === this.password);
      if (foundUser) {
        this.userService.setUsername(foundUser.username);
        this.router.navigate(['']);
      } else {
        this.errorMessage = 'The data entered are not correct, please check them.';
      }
    });
  }
}




<div class="container">
  <div class="logo">
    <img class="img" src="../assets/logo.png" alt="logo" />
    <div class="form">
  </div>
  </div>
  <div class="row">
    <div class="col-12 col-sm-10 col-md-8 col-sm-offset-1 col-md-offset-2">>
    </div>
  </div>
    <form (ngSubmit)="login()">
      <div class="form-group">
        <label for="nombre">Usuario</label>
        <input type="text" class="form-control" name="nombre" id="form__nombre" [(ngModel)]="username">
      </div>
      <div class="form-group">
        <label for="password">Contraseña</label>
        <input type="password" class="form-control" name="password" id="form__password" [(ngModel)]="password">
      </div>
      <div class="form-group">
        <button type="submit" class="btn btn-primary">Acceder</button>
      </div>
      <div class="form-group forgot">
        <p>¿Olvidó su contraseña?</p>
        <p>Pulse en el <a href="#">Enlace</a></p>
      </div>
      <div class="form-group error-message" *ngIf="errorMessage">
        {{ errorMessage }}
      </div>
    </form>
  </div>
